mapper.update:
update airline
set takeoffDate=#{takeoffDate}, airline=#{airline}, flightName=#{flightName}, flightNum=#{flightNum}, dest=#{dest}, takeoffTime=#{takeoffTime}, gateAlpha=#{gateAlpha}, gateNum=#{gateNum}, takeoffTimeNew=#{takeoffTimeNew}, remark=#{remark}, managerTel=#{managerTel}, note=#{note}
where id=#{id}
